INSERT IGNORE INTO `wp_f0npwq3mss_posts` VALUES (20, 1, '2016-02-09 12:10:41', '2016-02-09 12:10:41', 'Have you ever loaded or stressed your app with 10,000 concurrent users? In terms of performance, we encourage our customers to check system\'s speed, scalability, and stability before their product goes to market.\r\n<strong>\r\nWhy do Performance Testing?</strong>\r\n<ul>\r\n	<li>• Evaluate product readiness</li>\r\n	<li>• Identify performance bottlenecks</li>\r\n	<li>• Determine if the application is stable under different loads</li>\r\n	<li>• Collect and provide application\'s speed, stability and scalability to stake holders</li>\r\n</ul>\r\n<strong>Performance Testing Phases</strong>\r\n<img alt=\'Performance Testing\' src=\'http://www.codoid.com/wp-content/uploads/2016/02/performance-testing.png\'/>', 'Performance Testing', '', 'publish', 'open', 'open', '', 'performance-testing', '', '', '2016-06-14 12:41:35', '2016-06-14 12:41:35', '', 0, 'http://www.codoid.com/?p=20', 0, 'post', '', 0); 
