INSERT IGNORE INTO `wp_f0npwq3mss_posts` VALUES (185, 1, '2016-03-10 14:10:03', '2016-03-10 14:10:03', 'Fillo is an Excel API for Java and you can query xls &amp; xlsx files. Now, it supports SELECT, UPDATE &amp; INSERT queries with or without WHERE clause.\r\n<br><br>\r\n<h3>Select</h3>\r\n<pre class="brush:java">Fillo fillo=new Fillo();\r\nConnection connection=fillo.getConnection("C:\\\\Test.xlsx");\r\nString strQuery="Select * from Sheet1 where ID=100 and name=\'John\'";\r\nRecordset recordset=connection.executeQuery(strQuery);\r\n\r\nwhile(recordset.next()){\r\nSystem.out.println(recordset.getField("Details"));\r\n}\r\n\r\nrecordset.close();\r\nconnection.close();\r\n</pre>\r\n<br>\r\n<h3>Update</h3>\r\n<pre class="brush:java">Fillo fillo=new Fillo();\r\nConnection connection=fillo.getConnection("C:\\\\Test.xlsx");\r\nString strQuery="Update Sheet1 Set Country=\'US\' where ID=100 and name=\'John\'";\r\n\r\nconnection.executeUpdate(strQuery);\r\n\r\nconnection.close();\r\n</pre>\r\n<br>\r\n<h3>Insert</h3>\r\n<pre class="brush:java">Fillo fillo=new Fillo();\r\nConnection connection=fillo.getConnection("C:\\\\Test.xlsx");\r\nString strQuery="INSERT INTO sheet4(Name,Country) VALUES(\'Peter\',\'UK\')";\r\n\r\nconnection.executeUpdate(strQuery);\r\n\r\nconnection.close();\r\n</pre>\r\n<br>\r\n<h3>Muliple Where conditions</h3>\r\n<pre class="brush:java">//This is an enhancement in Fillo-1.11, now you can mention multiple conditions in a query as shown below.\r\nRecordset recordset=connection.executeQuery("Select * from Sheet1 where column1=value1 and column2=value2 and column3=value3");\r\n</pre>\r\n<br>\r\n<h3>Where method</h3>\r\n<pre class="brush:java">Recordset recordset=connection.executeQuery("Select * from Sheet1").where("ID=100").where("name=\'John\'");\r\n</pre>\r\n<br>\r\n<h3>Set table starting row and column</h3>\r\n<pre class="brush:java">//Now you can set table start row and column\r\nSystem.setProperty("ROW", "5");//Table start row\r\nSystem.setProperty("COLUMN", "3");//Table start column\r\nFillo fillo=new Fillo();\r\nConnection connection=fillo.getConnection(strFile);\r\n</pre>\r\n<br>\r\n<h3>Version</h3>\r\nFillo-1.12\r\n<br><br>\r\n<h3>License</h3>\r\n<a href="http://www.apache.org/licenses/LICENSE-2.0">Apache License, Version 2.0</a>\r\n<br><br>\r\n<h3>Email us, if you face any issues</h3>\r\nsupport@codoid.com', 'Fillo', '', 'publish', 'open', 'open', '', 'fillo', '', '', '2016-06-14 15:57:26', '2016-06-14 15:57:26', '', 0, 'http://www.codoid.com/?p=185', 0, 'post', '', 0); 
