INSERT IGNORE INTO `wp_f0npwq3mss_posts` VALUES (190, 1, '2016-03-10 14:18:58', '2016-03-10 14:18:58', 'StringDictionary is a Hash Table; it is designed to accept only strings. It could be used to handle large amounts of strings and retrieves item very quickly since it is a Hash Table. Let us see how we can add items to the StringDictionary object and retrieve it.\r\n<br><br>\r\n<h3>Code</h3>\r\n<pre class="brush:vb;ruler: true">Dim oStringDict\r\n\r\nSet oStringDict=DotNetFactory.CreateInstance("System.Collections.Specialized.StringDictionary")\r\n\r\noStringDict.Add "City1","London"\r\noStringDict.Add "City2","Paris"\r\n\r\nMsgbox oStringDict.Item("City1")\r\n\r\nSet oStringDict=Nothing\r\n</pre>\r\n<br><br>\r\n<h4>Tags</h4>\r\nDictionary Object, .net StringDictionary\r\n<br>', 'String Dictionary', '', 'publish', 'open', 'open', '', 'string-dictionary', '', '', '2016-03-10 14:18:58', '2016-03-10 14:18:58', '', 0, 'http://www.codoid.com/?p=190', 0, 'post', '', 0); 
