INSERT IGNORE INTO `wp_f0npwq3mss_posts` VALUES (236, 1, '2016-03-10 15:38:59', '2016-03-10 15:38:59', ' <h3>Identifying using ID attribute</h3>\r\n<pre class="brush:vb;ruler: true">obj.WebEdit("xpath:=//input[@id=\'htmlID\']").Set "QTP"  \r\n</pre>\r\n<br>\r\n<h3>Identifying using Type attribute</h3>\r\n<pre class="brush:vb;ruler: true">\'Setting value in first textBox\r\nobj.WebEdit("xpath:=//input[@type=\'text\'][1]").Set "QTP"\r\n</pre>\r\n<br>\r\n<h3>Identifying using Tagname</h3>\r\n<pre class="brush:vb;ruler: true">\'Highlighting first listBox\r\nobj.WebList("xpath:=//select[1]").Select "London"\r\n</pre>\r\n<br>\r\n<h3>Identifying using Innertext</h3>\r\n<pre class="brush:vb;ruler: true">\'Clicking Google link\r\nobj.Link("xpath:=//a[.=\'Google\']").Click\r\n</pre>\r\n<br>\r\n<h3>Identifying WebTable based on Rowcount</h3>\r\n<pre class="brush:vb;ruler: true">\'Highlighting the table which has only two rows\r\nobj.WebTable("xpath:=//table[count(.//tr)=2]").Highlight\r\n</pre>', 'XPath Examples for QTP Web Objects', '', 'publish', 'open', 'open', '', 'xpath-examples-for-qtp-web-objects', '', '', '2016-03-10 15:38:59', '2016-03-10 15:38:59', '', 0, 'http://www.codoid.com/?p=236', 0, 'post', '', 0); 
