INSERT IGNORE INTO `wp_f0npwq3mss_posts` VALUES (243, 1, '2016-03-10 15:44:28', '2016-03-10 15:44:28', ' Validating Sorted WebList. The below code excludes default selected value (i.e. --Choose One--) from validation.\r\n<br><br>\r\n<h3>Code</h3>\r\n<pre class="brush:vb" name="code">Dim oPage:Set oPage=Browser("name:=QTP").Page("title:=QTP")\r\n\r\narrCtry=Split(oPage.WebList("name:=select1").GetROProperty("all items"),";")\r\n&nbsp;\r\nSet objArray=DotNetFactory.CreateInstance("System.Collections.ArrayList","")\r\n&nbsp;\r\nFor i=0 to Ubound(arrCtry)\r\nIf arrCtry(i)&lt;&gt;"--Choose One--" Then\r\nobjArray.Add(arrCtry(i))\r\nEnd If\r\nNext\r\n&nbsp;\r\nobjArray.Sort()\r\nobjArray.Insert 0,"--Choose One--"\r\n&nbsp;\r\nFor j=0 to Ubound(arrCtry)\r\nstrOuput=strOuput+objArray(j)\r\nstrOuput=strOuput+";"\r\nNext\r\n&nbsp;\r\nIf strOuput=oPage.WebList("name:=select1").GetROProperty("all items")+";" Then\r\nMsgbox "The Weblist\'s values are sorted in alphabetical order"\r\nElse\r\nMsgbox "The Weblist values are not sorted in alphabetical order"\r\nEnd If\r\n</pre>', 'Validating Sorted WebList Values', '', 'publish', 'open', 'open', '', 'validating-sorted-weblist-values', '', '', '2016-03-10 15:44:28', '2016-03-10 15:44:28', '', 0, 'http://www.codoid.com/?p=243', 0, 'post', '', 0); 
