INSERT IGNORE INTO `wp_f0npwq3mss_posts` VALUES (472, 1, '2016-05-24 07:29:28', '2016-05-24 07:29:28', 'Testing APIs manually for every application release increases testing time and does not give confidence enough on build quality.\n\nThinking automation for API testing is right idea, but it requires good framework setup, utilities, right tool and skilled resources. We, at Codoid, use SoapUI, JMeter and custom framework & utilities for API automation testing based on requirements.\n\n<img alt="webservice automation testing" src=\'http://www.codoid.com/wp-content/uploads/2016/02/api-automation-testing.png\'></img>', 'API Automation', '', 'inherit', 'closed', 'closed', '', '32-autosave-v1', '', '', '2016-05-24 07:29:28', '2016-05-24 07:29:28', '', 32, 'http://www.codoid.com/32-autosave-v1/', 0, 'revision', '', 0); 
